package gradle.playground.app;

import gradle.playground.list.LinkedList;

import static gradle.playground.utilities.StringUtils.join;
import static gradle.playground.utilities.StringUtils.split;
import static gradle.playground.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
